(function(){
  // ① dev = [UIDevice currentDevice]
  var dev  = ObjC.invoke({class:'UIDevice', isClass:true, selector:'currentDevice', args:[]});
  // ② uuid = dev.identifierForVendor
  var uuid = ObjC.invoke({target:dev, selector:'identifierForVendor', args:[]});
  // ③ idfv = [uuid UUIDString]
  var idfv = ObjC.invoke({target:uuid, selector:'UUIDString', args:[]});
  // ④ ac = [UIAlertController alertControllerWithTitle:message:preferredStyle:]
  var title = '设备IDFV';
  var msg   = 'IDFV: ' + idfv;
  var ac = ObjC.invoke({class:'UIAlertController', isClass:true, selector:'alertControllerWithTitle:message:preferredStyle:', args:[{type:'string',value:title},{type:'string',value:msg},{type:'i',value:1}]});
  // ⑤ ok = [UIAlertAction actionWithTitle:style:handler:]
  var ok = ObjC.invoke({class:'UIAlertAction', isClass:true, selector:'actionWithTitle:style:handler:', args:[{type:'string',value:'OK'},{type:'i',value:0},{type:'nil', value:null}]});
  // ⑥ [ac addAction:ok]
  ObjC.invoke({target:ac, selector:'addAction:', args:[{type:'object', value:ok}]});
  // ⑦ topVC = [__QMDemoHelper topVC]，present（UI 放主线程）
  var topVC = ObjC.invoke({class:'__QMDemoHelper', isClass:true, selector:'topVC', args:[]});
  ObjC.invoke({target:topVC, selector:'presentViewController:animated:completion:', args:[{type:'object',value:ac},{type:'bool',value:true},{type:'nil',value:null}], thread:'main'});
  return idfv;
})()
